import logo from './logo.svg';
import './App.css';
import { BrowserRouter, Route, Routes } from 'react-router-dom';
import Products from './components/Products';
import ProductDetails from './components/ProductDetails';
import Cart from './components/Cart';
import Header from './components/Header';
import { useState } from 'react';
import Admin from './components/Admin';
const mongoose = require('mongoose');
function App() {
  const [ cartItems, setCartItems] = useState([])
  const handleAddToCart = (product) => {
    setCartItems([...cartItems, product]);
  }
  const handleCheckout = () => {
    setCartItems([])
  }
  return (
    <BrowserRouter >
      <Header cartItems={cartItems}/>
      <Routes>
          <Route path='/' element={<Products handleAddToCart={handleAddToCart} />}/>
          <Route path='/detail/:id' element={<ProductDetails handleAddToCart={handleAddToCart}/>}/>
          <Route path='/cart' element={<Cart cartItems={cartItems} handleCheckout={handleCheckout}/>}/>
          <Route path='/admin' element={<Admin />} />
      </Routes>
    </BrowserRouter>
  );
}
mongoose.connect('mongodb://localhost:27017/2274802010643_trantienphat_th5', {
  useNewUrlParser: true,
  useUnifiedTopology: true
}).then(() => {
  console.log('Connected to MongoDB via Compass');
}).catch(err => {
  console.error('Database connection error:', err);
});

export default App;
