import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Admin.css'; // Tạo file CSS mới để định dạng

function Admin() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = () => {
    // Giả lập kiểm tra tài khoản
    if (username === 'admin' && password === '123456') {
      setIsLoggedIn(true);
      navigate('/'); // Chuyển hướng sang trang chủ sau khi đăng nhập
    } else {
      alert('Tài khoản hoặc mật khẩu không đúng!');
    }
  };

  if (!isLoggedIn) {
    return (
      <div className="admin-container">
        <div className="admin-form">
          <h2>Admin Login</h2>
          <div>
            <label>
              Tài khoản:
              <input
                type="text"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                placeholder="phattran125004"
              />
            </label>
          </div>
          <div>
            <label>
              Mật khẩu:
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="125004"
              />
            </label>
          </div>
          <button onClick={handleLogin}>Login</button>
        </div>
      </div>
    );
  }

  return null; // Khi đăng nhập thành công, không cần hiển thị gì thêm ở trang này
}

export default Admin;
